mapper.select:
select u.user_id userId,u.username,u.email,u.mobile,u.status,u.crate_time crateTime, u.ssex,u.seat_number seatNumber, u.name, u.dept_id deptId, u.view_range viewRange, ( select listagg(role_id,',') within group (order by role_id) as role_ids from tb_yofishdk_auth_user_role ur where ur.user_id = u.USER_ID ) roleIds2 from tb_yofishdk_auth_user u where u.is_delete = 0
AND lower(u.username) = #{username}
AND u.ssex = #{ssex}
AND u.status = #{status}
order by u.user_id

mapper.update:
update tb_yofishdk_auth_user
set dept_id = NULL
where dept_id = #{deptId}

mapper.select:
select USER_ID userId, USERNAME username, NAME name, DEPT_ID deptId , EMAIL email, MOBILE mobile , SSEX ssex, IS_DELETE isDelete, IS_SEPERATE isSeperate from TB_YOFISHDK_AUTH_USER where IS_DELETE=0
and USER_ID in
#{item}
and IS_SEPERATE = #{isSeperate}
order by CRATE_TIME desc
