mapper.select:
  		select u.user_id userId,u.username,u.email,u.mobile,u.status,u.crate_time crateTime,
  		u.ssex,d.dept_name deptName,u.name
	    from tb_cl_privilege_user u
  		left join tb_cl_privilege_dept d on(u.dept_id = d.dept_id)
  		where 1=1

        	AND lower(u.username) = #{username}


	        AND d.dept_id = #{deptId}


	        AND u.ssex = #{ssex}


	        AND u.status = #{status}

  		order by u.user_id
