mapper.select:
  		select
			u.user_id userId,u.username,u.email,u.mobile,u.status,u.crate_time crateTime,
			u.ssex,d.dept_name deptName,u.name,u.seat_number seatNumber,
		   ( select
			  listagg(role_id,',') within group (order by role_id)  as  role_ids
			  from tb_yofishdk_auth_user_role ur
		 	 where ur.user_id = u.USER_ID
		    )  roleIds2
	    from tb_yofishdk_auth_user u
  		left join tb_yofishdk_auth_dept d on(u.dept_id = d.dept_id and d.is_delete = 0)
  		where u.is_delete = 0

        	AND lower(u.username) = #{username}


	        AND d.dept_id = #{deptId}


	        AND u.ssex = #{ssex}


	        AND u.status = #{status}

  		order by u.user_id

mapper.update:
update tb_yofishdk_auth_user
set dept_id = NULL
where dept_id = #{deptId}
