mapper.select:
select u.user_id userId,u.username,u.email,u.mobile,u.status,u.crate_time crateTime, u.ssex,u.seat_number seatNumber, u.name, u.dept_id deptId, u.view_range viewRange, ( SELECT GROUP_CONCAT(role_id SEPARATOR ',') FROM TB_YOFISHDK_AUTH_USER_ROLE ur GROUP BY ur.user_id HAVING ur.user_id = u.user_id ) roleIds2 from TB_YOFISHDK_AUTH_USER u where u.is_delete = 0
AND u.username = #{username}
AND u.ssex = #{ssex}
AND u.status = #{status}
order by u.user_id

mapper.update:
update TB_YOFISHDK_AUTH_USER
set dept_id = NULL
where dept_id = #{deptId}

mapper.select:
select USER_ID userId, USERNAME username, NAME name, DEPT_ID deptId , EMAIL email, MOBILE mobile , SSEX ssex, IS_DELETE isDelete, IS_SEPERATE isSeperate from TB_YOFISHDK_AUTH_USER where IS_DELETE=0
and USER_ID in
#{item}
and IS_SEPERATE = #{isSeperate}
order by CRATE_TIME desc
